mapper.select:
        SELECT
          c.id, c.fullname, dv1.text as appellation, u1.name as owner, c.company, c.job, c.email, c.phone, c.website,
          c.mphone, dv3.text as state, dv2.text as source, u2.name as create_by, c.create_time, u3.name as edit_by, c.edit_time, c.description, c.contact_summary,c.next_contact_time, c.address
        FROM tbl_clue c join tbl_user u1 on c.owner = u1.id 
                        join tbl_user u2 on c.create_by = u2.id
                        left join tbl_user u3 on c.edit_by = u3.id
                        left join tbl_dic_value dv1 on c.appellation = dv1.id
                        left join tbl_dic_value dv2 on c.source = dv2.id
                        left join tbl_dic_value dv3 on c.state = dv3.id




		        fullname like #{fullnamePattern}



		        and company like #{companyPattern}



		        and phone like #{phonePattern}


		        and source = #{source}



		        and u1.name like #{ownerPattern}



		        and mphone like #{mphonePattern}


		        and state = #{state}


        order by c.create_time desc
        limit #{beginNo},#{pageSize}

mapper.delete:
delete from tbl_clue
where id in

    #{id}
